cssVariables:
:root {
  /* Colors */
  --color-white: #FFFFFF;
  --color-black: #1A1A1A;
  --color-gray: #6B7280;
  --color-primary: #3B82F6;
  --color-success: #22C55E;
  --color-error: #EF4444;

  /* Semantic */
  --bg: var(--color-white);
  --fg: var(--color-black);
  --muted: var(--color-gray);
  --accent: var(--color-primary);

  /* Spacing */
  --space-xs: 0.25rem;
  --space-sm: 0.5rem;
  --space-md: 1rem;
  --space-lg: 1.5rem;
  --space-xl: 2rem;
  --space-2xl: 4rem;

  /* Typography */
  --font-sans: system-ui, -apple-system, sans-serif;
  --font-mono: ui-monospace, monospace;
  --text-sm: 0.875rem;
  --text-base: 1rem;
  --text-lg: 1.25rem;
  --text-xl: 1.5rem;
  --text-2xl: 2rem;

  /* Radius */
  --radius-sm: 0.25rem;
  --radius-md: 0.5rem;
  --radius-full: 9999px;

  /* Shadow */
  --shadow-sm: 0 1px 2px rgba(0,0,0,0.05);
  --shadow-md: 0 4px 6px rgba(0,0,0,0.1);
}
